sp_widget.client_script:
function($scope, spUtil, spModal, $rootScope) {
  /* widget controller */
  var c = this;
	console.log(c.data.list);
	$scope.propertySelected = function(value){
		//spModal.alert('The value is ' + value);
		//console.log("propertyEditable: " + $rootScope.propertyEditable);
		if(c.selected){			
			spModal.alert("Property is ediatable. Please 'Save' or 'Cancel' before selecting any property");
		}else{
			var idx;
			var prop = {};
			console.log(value);
			for(idx in c.data.list){
				//console.log(prop);
				if(c.data.list[idx].number == value){
					prop.number = c.data.list[idx].number;
					prop.title = c.data.list[idx].title;
					prop.address = c.data.list[idx].address;
				}
			}
			$rootScope.$emit('propSelected', prop);		
		}

	};

	$rootScope.$on('propertyEditable', function(event, data){
		console.log(data);
		if(data.selected){
			c.selectedPropertyNumber = data.number;
		}else{
			c.selectedPropertyNumber = null;
		}
		c.selected = data.selected;		
	});

	$rootScope.$on('propertySaved', function(event, data){
		//console.log('Inside propertySaved event');
		//console.log(data);
		//console.log(c.data.list);
		for(var i = 0; i < c.data.list.length; i++){
			console.log(c.data.list[i].number + '=?=' + data.number);			
			if(c.data.list[i].number == data.number){
				c.data.list[i] = data;
			}
		}
	}); 
}

