sys_script_include.script:
var Property = Class.create();
Property.prototype = {
	initialize: function() {
	},
	getList: function(){
		var properties = [];
		var rec = new GlideRecord('x_58872_needit_property');
		rec.query();
		while(rec.next()){
			var prop = {};		
			prop.number = rec.getValue('number');
			prop.title = rec.getValue('title');
			prop.address = rec.getValue('address');		
			properties.push(prop);		
		}

		var reportService = new PropertyReport();
		var propRatings = reportService.getReportRatings();
		var p;
		for(p in properties){
			var ratingList = propRatings[properties[p].number];
			if(ratingList){
				properties[p].ratings = ratingList;
			}			
		}
		return properties;
	},
	getListById: function(number){
		var rec = new GlideRecord('x_58872_needit_property');
		rec.addQuery('number', number);
		rec.query();		
		var prop = {};
		if(rec.next()){		
			prop.number = rec.getValue('number');
			prop.title = rec.getValue('title');
			prop.owner = rec.getValue('owner');
			prop.address = rec.getValue('address');
			prop.url = rec.getValue('imageurl');
		}
		return prop;
	},
	updateProperty: function(property){
		var rec = new GlideRecord('x_58872_needit_property');
		rec.addQuery('number', property.number);
		rec.query();
		if(rec.next()){
			rec.setValue('title', property.title);
			rec.setValue('owner', property.owner);
			rec.setValue('address', property.address);
			rec.update();
		}
		return property;
	},
	type: 'Property'
};
