sys_script_include.script:
var PropertyReport = Class.create();
PropertyReport.prototype = {
	initialize: function() {
	},

	getReports: function(propertyNumber, columns){
		var rec = new GlideRecord('x_58872_needit_inspection_report');
		rec.addQuery('property_number', propertyNumber);
		rec.orderBy('sys_created_on');
		rec.query();
		var data = [];
		while(rec.next()){
			var report = {};
			for(var i = 0; i < columns.length; i++){
				report[columns[i]] = rec.getValue(columns[i]);
			}
			data.push(report);
		}
		return data;
	},

	getReportById: function(reportNumber){

	},

	getReportRatings: function(){
		var recProp = new GlideRecord('x_58872_needit_inspection_report');
		recProp.orderBy('sys_created_on');
		recProp.query();
		var result = {};
		while(recProp.next()){
			var propNum = recProp.getValue('property_number');
			if(!result[propNum]){
				result[propNum] = [];
			}
			result[propNum].push(recProp.getValue('rating'));
		}
		return result;
	},

	type: 'PropertyReport'
};
